import React, { useState } from "react";
import "./style.css";
import flower from "../images/flower.png";
const MainPage = () => {
  const [noBtnValue, setNoBtnValue] = useState("No");
  const [counter, setCounter] = useState(0);
  const noList = [
    // { 1: "No" },
    // { 2: "Must be a misclick, try again" },
    // { 3: "Ouch ðŸ’”, that hurt. Must have misclicked again, try again." },
    // { 4: "How could you ðŸ¥¹? Please think again" },
    // { 5: "Maybe reconsider once again ðŸ¥¹ðŸ‘‰ðŸ‘ˆ" },
    // { 6: "You are breaking my heart now ðŸ˜­ðŸ’”. Maybe think one last time." },
    { key: 1, value: "Must be a misclick, try again" },
    {
      key: 3,
      value: "Ouch ðŸ’”, that hurt. Must have misclicked again, try again.",
    },
    { key: 4, value: "How could you ðŸ¥¹? Please think again" },
    { key: 5, value: "Maybe reconsider once again ðŸ¥¹ðŸ‘‰ðŸ‘ˆ" },
    {
      key: 6,
      value: "You are breaking my heart now ðŸ˜­ðŸ’”. Maybe think one last time.",
    },
  ];
  const handleNo = () => {
    setCounter(counter + 1);
    if (counter < 7) {
      noList.map((element) => {
        if (element.key === counter) {
          setNoBtnValue(element.value);
        }
      });
    }
  };
  return (
    <div id="main">
      <div id="container">
        <img
          src={flower}
          width={"200px"}
          height={"200px"}
          style={{ margin: "20px" }}
        />
        <h2>Will you be my valentine bb?</h2>
        <div id="button-container">
          <button type="button" id="yes-btn" className="buttons">
            Yes
          </button>
          <button
            type="button"
            id="no-btn"
            className="buttons"
            onClick={handleNo}
          >
            {noBtnValue}
          </button>
        </div>
      </div>
    </div>
  );
};
export default MainPage;
